void getLog(float melEnergies[N_MEL]) {
    for (uint8_t i = 0; i < N_MEL; i++)
      melEnergies[i] = log(melEnergies[i] + 1e-6f);
}  

void getMFCC(float window[WIN_SIZE], float mfccArray[N_MFCC]) {
    float melEnergies[N_MEL];
    float mfcc;
    float energy;

    for (uint8_t i = 0; i < N_MEL; i++) { // time = 4ms
        energy = 0.0f;
        for (uint16_t j = 0; j < WIN_SIZE / 2; j++)
            energy += window[j] * melFilters[i][j];
        melEnergies[i] = energy;
    }

    getLog(melEnergies); // time = 0ms

    // for (uint8_t i = 0; i < N_MEL; i++) {
    //     Serial.print(melEnergies[i], 6);
    //     if (i < N_MEL - 1) Serial.print(",");
    // }
    // Serial.println();  // Fin d'une trame

    for (uint8_t i = 0; i < N_MFCC; i++) { // time = 1ms
        mfcc = 0.0f;
        for (uint8_t j = 0; j < N_MEL; j++)
            mfcc += melEnergies[j] * dctMatrix[i][j];
        mfccArray[i] = mfcc;
    }
}